var permission = $evaluation.getPermission();
var res = permission.getResource();
if (res) {
    var attrs = res.getAttributes();
    if(attrs.containsKey('visibility') && 
       attrs.get('visibility').get(0).equals('private')) {
        $evaluation.deny();
        permission.addClaim('visibility', 'sensor is not visible');
      } else {
        $evaluation.grant();
        permission.addClaim('visibility', 'sensor is visible');
    }
} else {
    $evaluation.grant();
}

